import React from 'react'
import Player from '../player'
import Map from '../map'

import { tiles } from '../../data/maps/1'
import store from '../../config/store'

function World(props) {
    store.dispath({ type: 'ADD_TILES', payload: {
        tiles
    }})
    return (
        <div
            style={{
                position: 'relative',
                width: '800px',
                height: '400px',
                margin: '20px auto',
            }}
        >
            <Map />
            <Player />
        </div>
    )
}

export default World
