const initialState = {
    tiles: [],
    treasureFound: false,
}

const mapReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'ADD_TILES':
            return {
                ...action.payload,
            }
        case 'TREASURE_FOUND':
            return {
                ...state,
                ...action.payload
            }
        default:
            return state
    }
}

export default mapReducer
